finishtests.finishtest:
import static org.junit.Assert.*;
import org.junit.Test;
public class FinalTestRomanNumbers
{
	@Test
	public void test1() 
	{
		assertEquals(RomanNumberConverter.convert(1), "I");
		assertEquals(RomanNumberConverter.convert(2), "II");
		assertEquals(RomanNumberConverter.convert(3), "III");
		assertEquals(RomanNumberConverter.convert(4), "IV");
		assertEquals(RomanNumberConverter.convert(5), "V");
		assertEquals(RomanNumberConverter.convert(9), "IX");
		assertEquals(RomanNumberConverter.convert(10), "X");
		assertEquals(RomanNumberConverter.convert(15), "XV");
		assertEquals(RomanNumberConverter.convert(20), "XX");
		assertEquals(RomanNumberConverter.convert(40), "XL");
		assertEquals(RomanNumberConverter.convert(45), "XLV");
		assertEquals(RomanNumberConverter.convert(50), "L");
	}

}
